// Generated by Selenium IDE
const { Builder, By, Key, until } = require('selenium-webdriver')
const assert = require('assert')

describe('CP010 - INTERACCIONES', function() {
  this.timeout(30000)
  let driver
  let vars
  beforeEach(async function() {
    driver = await new Builder().forBrowser('chrome').build()
    vars = {}
  })
  afterEach(async function() {
    await driver.quit();
  })
  it('CP010 - INTERACCIONES', async function() {
    // Test name: CP010 - INTERACCIONES
    // Step # | name | target | value
    // 1 | open | /home.php | 
    await driver.get("https://tucan.toolsincloud.net/home.php")
    // 2 | setWindowSize | 697x720 | 
    await driver.manage().window().setRect({ width: 697, height: 720 })
    // 3 | click | css=.grid-sidebar:nth-child(5) strong | 
    // NOTIFICATION TAB
    await driver.findElement(By.css(".grid-sidebar:nth-child(5) strong")).click()
    // 4 | assertElementPresent | css=.home-name | Notifications
    // LISTA DE NOTIFICACIONES
    {
      const elements = await driver.findElements(By.css(".home-name"))
      assert(elements.length)
    }
  })
})
